        bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(REVOKE_TYPEHASH, uuid, nonce)));

        if (ECDSA.recover(digest, v, r, s) != attester) {
            revert InvalidSignature();
        }
    }
}
